import { actionTypes } from '../actions/items.action';

function indexShow(item) {

  const retorno = {
                  id: [item.id], 
                  nome: [item.nome],
                  desc: [item.desc]
                }
  return retorno;
}

const initialState = {
  items: {

  },
  item: {

  },
  success: false,
  error: {}
}

export default (state = initialState, { type, data, isLoadMore }) => {
  switch (type) {

    case actionTypes.CHANGE:
      return { 
        ...state,
        item: {
          ...state.item,
          data
        }
      }
    
    case actionTypes.INDEX:
      if(isLoadMore){
        console.log(data)
        data.items = data.map((item) => indexShow(item))
        console.log(data.items);
      } else {
        console.log('n foi')
      }
      return { ...state, ...data }

    case actionTypes.DESTROY:
      return {
        ...state,
        items:{
          ...state.items,
          data: state.items.data.filter(item => item.id !== data)
        }
      }

    case actionTypes.SUCCESS:
      return { ...state, success: data }

    case actionTypes.ERROR:
      return { ...state, error: data }

    default:
      return state
  }
}
