  }

  useEffect (() => {
    if (!items.LOADING){
      for(let i = 0; i < items.ITEMS.length; i = i + 1 ) {
        dispatch( photo( items.ITEMS[i].id ) )
      }
    }
  }, [items.LOADING]);

  useEffect( () => {
    indexCall();
  }, []);

  return (
    <div className="container pt-5">
      <div className="row justify-content-center">
        <div className="col-auto">
          <h1>
            Ãšltimas novidades
          </h1>
        </div>
      </div>
      <div className="row justify-content-center">
        {items.LOADING ? <CircularProgress />
        :
        <>
          {items.ITEMS.map( (item) =>
            <div onClick={handleItemClick} className='col-md-4 justify-content-center d-flex card'>
              <div className='row d-flex flex-row justify-content-center'>
                {items.LOADING_PHOTO ? <CircularProgress />
                :
                  <>{items.PHOTO.map( (photo) => <>{photo.id !== item.id ? ''
                    :

                    <img className='p-2 foto' src={'http://api-lojabim.test/'.concat([photo.img])}></img>
                  }</>)}</>
                }
              </div>
              <div className='row d-flex flex-row desc'>
                <div className="pl-5">
                  {item.desc}
                </div>
              </div>
              <div className='row d-flex flex-row preco'>
                <div className="pl-5">
                  R${item.preco}
                </div>
              </div>
            </div>
          )}
        </>
        }
        {console.log(cart)}
      </div>
    </div>
  )
}
