    <>
      <Register open={signUpShow}/>    
      <Modal show={open} onHide={handleClose}>
        <Modal.Header closeButton>    
          <h2 className='pl-5'>
            Faça o login
          </h2>
        </Modal.Header>
        <Modal.Body>
          <Container>
            <Row>
              <Col>
                <TextField
                label='Email'
                type='email'
                autoComplete='email'
                margin='normal'
                fullWidth
                value={credentials.email}
                onChange={text => dispatch(changeAuth({email: text.target.value}))}
                />  
              </Col>
            </Row>
            <Row>
              <Col>
                <TextField
                label='Senha'
                type='password'
                margin='normal'
                fullWidth
                value={credentials.password}
                onChange={text => dispatch(changeAuth({password: text.target.value}))}
                />
              </Col>
            </Row>
            <Row>
              <Col>
                <Button
                variant='contained'
                color='primary'
                fullWidth
                size='large'
                className='mt-4 mb-4'
                onClick={ () => handleLogin() }
                >
                  Logar
                </Button>
              </Col>
            </Row>
          </Container>
        </Modal.Body>
        <Modal.Footer>
          <Container>
            <Row>
              <Col className='d-flex justify-content-center'>
                Não tem conta? Cadastre-se!
              </Col>
            </Row>
          </Container>
        </Modal.Footer>
      </Modal>
    </>
  )}
