import { Link } from "react-router-dom";
import style from "./style.css";
import PersonIcon from '@material-ui/icons/Person';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import { index } from '../../store/actions/me.action';
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from 'react';
import { CircularProgress } from '@material-ui/core';
import Cart from '../cart';

export default function Header() {

  const me = useSelector (state => state.Me);
  const dispatch = useDispatch();

  const indexCall = () => {
    dispatch( index() );

  }

  useEffect( () => {
    indexCall();
  }, []);

  const [cartOpen, setCartOpen] = useState(true);

  const changeCartStatus = () => {
    if (cartOpen === true) {
      return false;
    }
  }

  return (
    <>
      <Cart cartActive={cartOpen}/>
      {(window.innerWidth < 577) ?
        <></>
        :
        <div className='container-fluid navbar'>
          <header className='row navbar'>
            <div className='col-8 d-flex logo'>
              [LOGO IMG]
            </div>
            <div className='col-4 row'>
              <div className='col-6 d-flex justify-content-center pt-1'>
              {(me.LOADING) ?
                  <div className="d-flex jultify-content-center mt-5 pt-5">
                    <CircularProgress />
                  </div>
                  :
                  <>{(me.NAME) ?
                      <>Ol√°, {me.NAME}</>                  
                      : 
                      <Link to='/login'><PersonIcon fontSize='default'/> Login</Link>
                    }</>
              }
                
              </div>
              <div className='col-6 d-flex justify-content-center pt-1'>
                <ShoppingCartIcon fontSize='default' onClick={ (e)=>{
                  setCartOpen();
                }}/>
              </div>
            </div>
          </header>
        </div>  
        }
    </>
  )
}
