import { Http } from '@material-ui/icons';
import { HttpAuth } from '../../config/Http';

export const actionTypes = {
  INDEX_ITEMS: 'INDEX_ITEMS',
  INDEX_LOADING: 'ITEMS_LOADING',
  INDEX_PHOTO: 'ITEMS_PHOTO',
  LOADING_PHOTO: 'LAOADING_PHOTO'
}

export const index = () => dispatch => {
  return HttpAuth.get('/item')
    .then( (response) => {

      dispatch({
        type: actionTypes.INDEX_ITEMS,
        payload: response.data
      });
      
      dispatch({
        type: actionTypes.INDEX_LOADING,
        payload: false
      })
      
    })
}

export const photo = () => dispatch => {

  dispatch({
    type: actionTypes.INDEX_LOADING,
    payload: true
  });

  return HttpAuth.get('/foto').then(  (response) => {
    console.log(response);
    dispatch({
      type: actionTypes.INDEX_PHOTO,
      payload: response.data
    });
  })
}
