import React, { useEffect, useState, useLayoutEffect } from 'react';
import { CircularProgress } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { useSelector, useDispatch } from 'react-redux';
import { index, photo } from '../../store/actions/items.action';
import { add_items, remove_items, get_cart } from '../../store/actions/cart.action';
import { style } from './style.css';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import { changeNotify } from '../../store/actions/notify.action';
import styled from 'styled-components';
import ShowPhotos from '../showPhotos';
import { ButtonGroup, Button } from 'react-bootstrap';
import { openedItems } from '../openedItems';

const Categories = styled.div`
  min-height: 6vh;
  background-color: #474a51;
  color: white;
`;

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: '80%',
    height: '60%',
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    top: '20%',
    left: '10%'
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  }
}));

export default function BannerItems(){

  const dispatch = useDispatch();
  const items = useSelector(state => state.Items);

  //Seleçãode itens a ser mostrado
  const [tipoItens, setTipoItens] = useState(0);

  function indexCall() {
    dispatch( index(0) );
  }

  useEffect(()=>{
    dispatch( index(tipoItens) );
  },[tipoItens]);

  //Abertura de itens

  const classes = useStyles();

  // getModalStyle is not a pure function, we roll the style only on the first render
  const [open, setOpen] = useState(false);
  const [item, setItem] = useState({ 
    id: null,
    foto: null, 
    desc: null,
    preco: null
  });

  //Definidor de width da tela em tempo real
  const [width, setWidth] = useState(window.innerWidth);
  useLayoutEffect(() => {
    function updateSize() {
      setWidth(window.innerWidth);
    }
    window.addEventListener('resize', updateSize);
    updateSize();
    return () => window.removeEventListener('resize', updateSize);
  }, []);
  


  //input de tamanho e retorno
  const [tamanho, setTamanho] = React.useState(undefined);

  const handleChange = (event) => {
    setTamanho(event.target.value);
  };

  const handleOpen = (id, desc, preco, tipo) => {
    setOpen(true);
    setItem({ id: id,
              desc: desc,
              preco: preco,
              tipo: tipo
            });
  };

  const handleClose = () => {
    setOpen(false);
  };

  //!Abertura de itens

  useEffect (() => {
    if (!items.LOADING){

      let items_id = [];

      for(let i = 0; i < items.ITEMS.length; i = i + 1 ) {

        if (items.ITEMS[i].id !== undefined){
          items_id.push(items.ITEMS[i].id);
        }
      }

      dispatch( photo( items_id ) );

    }
  }, [items.LOADING]);

  useEffect( () => {
    indexCall();
  }, []);

  return (
    <div className="container pt-5">
      <Categories className='container-fluid'>
        <div className='row'>
          <ButtonGroup 
            size="lg" 
            className="mb-2 col-12 button-group"
            vertical={ width <= 768 ? true : false }
          >
            <Button variant="secondary" className='d-flex justify-content-center mb-3'
              onClick={(e)=>{setTipoItens(1)}}
            >
              Camisas Slim
            </Button>
            <Button variant="secondary" className='d-flex justify-content-center mb-3'
              onClick={(e)=>{setTipoItens(2)}}
            >
              Camisas Street
            </Button>
            <Button variant="secondary" className='d-flex justify-content-center mb-3'
              onClick={(e)=>{setTipoItens(3)}}
            >
              Calçados
            </Button>
            <Button variant="secondary" className='d-flex justify-content-center mb-3'
              onClick={(e)=>{setTipoItens(4)}}
            >
              Bermudas e calças
            </Button>
          </ButtonGroup>
        </div>
      </Categories>

      <div className="row justify-content-center">
        <div className="col-auto" onClick={(e)=>{setTipoItens(0)}}>
          <h1 className='mb-4'>
            {tipoItens === 0 && 'Últimas novidades'}
            {tipoItens === 1 && 'Camisas Slim'}
            {tipoItens === 2 && 'Camisas Street'}
            {tipoItens === 3 && 'Calçados'}
            {tipoItens === 4 && 'Bermudas e calças'}
          </h1>
        </div>
      </div>
      <div className="row justify-content-center">
        {items.LOADING ? <CircularProgress />
        :
        <>
          {typeof(items.ITEMS) === "object" ?
            items.ITEMS.map( (item) =>
              <div
                key={item.id}
                className='col-md-3 d-flex card flex-column justify-content-between'
              >
                <div className='row d-flex flex-row justify-content-center'>
                    {
                      items.LOADING_PHOTO ? <CircularProgress/> : <ShowPhotos id={item.id}/>
                    }
                </div>
                <span 
                  onClick={(e)=>handleOpen(item.id, item.desc, item.preco, item.tipo)}
                  className='apontador'
                >
                  <div className='row desc d-flex flex-row'>
                    <div className="pl-3 pr-3">
                      <center>{item.desc}</center>
                    </div>
                  </div>
                  <div className='preco'>
                    <center>
                      R$ 
                      <span className='real'>
                        {item.preco.split(".")[0]}
                      </span>
                      ,
                      <span className='centavos'>
                        {item.preco.split(".")[1]}
                      </span>
                    </center>
                  </div>
                </span>
              </div>
            )
            :
            <>
              Não há itens nessa categoria.
            </>
          }
        </>
        }
      </div>
    </div>
  )
}
