import React from 'react';
import { TextField, Typography, Button } from '@material-ui/core';
import {  useDispatch, useSelector } from 'react-redux';
import { changeRegister, register } from '../../store/actions/register.action';
import { Link, Redirect } from 'react-router-dom';

export default function Register() {

  const dispatch = useDispatch();
  const { user, error, success } = useSelector(state => state.Register);

  return (
    <div className='d-flex bg-white min-vh-100'>
      <div className='container mt-5'>
        <div className='row justify-content-center'>
          <div className='col-md-4'>
            <div className=' form-group text-center'>
              <Typography className='mt-2' variant='h6' component='h1'>
                Increva-se
              </Typography>
            </div>

            <TextField
              error = {(error.name) && true}
              margin = 'normal'
              label = 'Nome'
              value = {user.name}
              onChange = {text => {
                dispatch( changeRegister({ name: text.target.value }));
                if (error.name && delete error.name);
              }}
            />
            {(error.name) && 
              <strong className='text-danger'>{error.name[0]}</strong>
            }

            <TextField
              error = {(error.email) && true}
              margin = 'normal'
              label = 'Email'
              value = {user.email}
              type = 'email'
              autoComplete = 'email'
              onChange = {text => {
                dispatch( changeRegister({ email: text.target.value }));
                if (error.email && delete error.email);
              }}
            />
            {(error.email) && 
              <strong className='text-danger'>{error.email[0]}</strong>
            }

            <TextField
              error = {(error.password) && true}
              margin = 'normal'
              label = 'Senha'
              type = 'password'
              value = {user.password}
              onChange = {text => {
                dispatch( changeRegister({ password: text.target.value }));
                if (error.password && delete error.password);
              }}
            />
            {(error.password) && 
              <strong className='text-danger'>{error.password[0]}</strong>
            }

            <Button
              variant = 'contained'
              color = 'primary'
              fullWidth
              size = 'large'
              className = 'mt-4 mb-4'
              onClick = {() => dispatch( register(user) )}
            >
              Cadastrar
            </Button>

            <div className='text-center'>
              <Link to='/login' className='mt-4 text-danger'>
                Fazer Login
              </Link>
            </div>

            {(success) &&
              <Redirect to="/me" />
            }
          </div>
        </div>
      </div>
    </div>
  )
}
