export const actionTypes = {
  ADD_ITEM: 'ADD_ITEM',
  REMOVE_ITEM: 'REMOVE_ITEM'
}

export const ADD_ITEMS = ({id, qtd}) => ({
  type: actionTypes.ADD_ITEM,
  payload: {id: id, qtd: qtd}
})
