export const actionTypes = {
  ADD_ITEM: 'ADD_ITEM',
  REMOVE_ITEM: 'REMOVE_ITEM'
}

export const ADD_ITEMS = (payload) => ({
  type: actionTypes.ADD_ITEM,
  payload: {id: payload.id, qtd: payload.qtd},
})
