import React,{ useState, useEffect } from 'react';
import { Modal, Button, Form, Container, Row, Col } from 'react-bootstrap';
import { HttpAuth } from '../../../config/Http';
import Axios from 'axios';
import { CircularProgress } from '@material-ui/core';

export default function Endereco(props) {

  //Mecanismo de abertura e fechamento do modal
  const [open, setOpen] = useState(false);
  const [qtdEndereco, setQtdEndereco] = useState(1);
  const [endereco, setEndereco] = useState({
    rua: '',
    numero: '',
    complemento: '',
    bairro: '',
    cep: '',
    cidade: '',
    estado: ''
  });

  //valores dos inputs
  const [rua, setRua] = useState('');
  const [numero, setNumero] = useState('');
  const [complemento, setComplemento] = useState('');
  const [bairro, setBairro] = useState('');
  const [cep, setCep] = useState('');
  const [cidade, setCidade] = useState('');
  const [estado, setEstado] = useState('');
  const [sn, setSn] = useState(false);
  const [cepLoading, setCepLoading] = useState(true);
  const [cepIsValid, setCepIsValid] = useState(undefined);
  const [cepIsInvalid, setCepIsInvalid] = useState(undefined);
  const [enderecoSetadoByCep, setEnderecoSetadoByCep] = useState({
    rua: undefined,
    bairro: undefined,
    cidade: undefined,
    estado: undefined
  });
  const [ruaChecked, setRuaChecked] = useState(undefined);
  const [bairroChecked, setBairroChecked] = useState(undefined);
  const [cidadeChecked, setCidadeChecked] = useState(undefined);
  const [estadoChecked, setEstadoChecked] = useState(undefined);
  const setClickSn = (e)=>{
    if (sn === false){
      setSn(true);
    } else {
      setSn(false);
    }
  }

  useEffect(()=>{
    setRua(endereco.rua);
    setNumero(endereco.numero);
    setComplemento(endereco.complemento);
    setBairro(endereco.bairro);
    setCep(endereco.cep);
    setCidade(endereco.cidade);
    setEstado(endereco.estado);
  },[endereco]);

  const handleClose = () => {
    setOpen(false);
  };

  useEffect(()=>{
    if (props.open){
      setOpen(true);
      buscaEndereco();
    }
  },[props.open]);

  useEffect(()=>{

    setCepIsValid(undefined);
    setCepIsInvalid(undefined);

    if (cep !== undefined){
      if (cep.length === 8){

        setCepLoading(true);
        setTimeout(
        Axios.get('https://viacep.com.br/ws/'+cep+'/json/unicode/').then(response => {
          if(response.data.cep === undefined){
            setCepIsInvalid(true);
          } else {
            setCepIsValid(true);
            setEndereco({
              rua: response.data.logradouro,
              numero: '',
              complemento: '',
              bairro: response.data.bairro,
              cep: cep,
              cidade: response.data.localidade,
              estado: response.data.uf
            })

            
            if (response.data.logradouro !== undefined || response.data.logradouro !== ''){
              setRuaChecked(true);
            } else {
              setRuaChecked(false);
            }

            if (response.data.bairro !== undefined || response.data.bairro !== ''){
              setBairroChecked(true);
            } else {
              setBairroChecked(false);
            }

            if (response.data.localidade !== undefined || response.data.localidade !== ''){
              setCidadeChecked(true);
            } else {
              setCidadeChecked(true);
            }

            if (response.data.uf !== undefined || response.data.uf !== ''){
              setEstadoChecked(true);
            } else {
              setEstadoChecked(false);
            }

            setEnderecoSetadoByCep({
              rua: ruaChecked,
              bairro: bairroChecked,
              cidade: cidadeChecked,
              estado: estadoChecked
            });

            console.log(enderecoSetadoByCep)
            
          }
          setCepLoading(false);
        })
        ,10000);
      }
    }

    
  },[cep])

  function buscaEndereco (){
    HttpAuth.get('cliente/'+props.id+'/endereco').then(
      (response) => {
        if (response.data !== undefined || response.data !== '' || response.data !== '[]') {

          setQtdEndereco(response.data.length);
          
          setEndereco({
            rua: rua,
            numero: response.data[qtdEndereco-1].numero,
            complemento: response.data[qtdEndereco-1].complemento,
            bairro: bairro,
            cep: response.data[qtdEndereco-1].cep,
            cidade: cidade,
            estado: estado
          })
        }
      }
    );
  }

  return (<>
    <Modal show={open} onHide={handleClose} size='xl'>
        <Modal.Header closeButton>
          <h2>
            Endereço de entrega
          </h2>
        </Modal.Header>
        <Modal.Body>
          <Container>
            <Row>
              <Col>
                <Form>
                  <Form.Group controlId="endereco">
                    <Row>
                      <Col>
                        <Form.Label>Cep</Form.Label>
                        <Form.Control 
                        size='sm'
                        type="text"
                        placeholder="12345123" 
                        value={cep}
                        onChange={text => setCep(text.target.value)}
                        disabled={cepLoading && true}
                        isValid={cepIsValid}
                        isInvalid={cepIsInvalid}
                        />
                        {
                          cepLoading ? 
                            <CircularProgress/>
                              :
                            <Form.Text className="text-muted">
                              Caso não saiba seu cep: 
                              <a
                                href='https://buscacepinter.correios.com.br/app/endereco/index.php'
                              >
                                Pesquise seu cep
                              </a>
                            </Form.Text>
                        }
                        
                      </Col>
                      <Col>
                        <Form.Label>Rua</Form.Label>
                        <Form.Control
                          size='sm'
                          type="text"
                          placeholder="Rua ciclano da silva"
                          value={rua}
                          onChange={text => setRua(text.target.value)}
                          disabled={enderecoSetadoByCep.rua}
                        />
                        <Form.Label>Número</Form.Label>
                        <Form.Control
                          size='sm'
                          type="number"
                          placeholder="10"
                          value={numero}
                          onChange={text => setNumero(text.target.value)}
                          disabled={sn}
                          
                        />
                        <Form.Check 
                          type="switch"
                          id="isSemNumero"
                          label="s/n"
                          value={sn}
                          onClick={(e) => setClickSn()}
                        />
                      </Col>
                    </Row>
                    <Form.Label>Complemento</Form.Label>
                    <Form.Control
                      size='sm'
                      type="text"
                      placeholder="Ap 1001"
                      value={complemento}
                      onChange={text => setComplemento(text.target.value)}
                    />
                    <Form.Label>Bairro</Form.Label>
                    <Form.Control
                      size='sm'
                      type="text"
                      placeholder="Bairro das flores"
                      value={bairro}
                      onChange={text => setBairro(text.target.value)}
                      disabled={enderecoSetadoByCep.bairro}
                    />
                    <Form.Label>Cidade</Form.Label>
                    <Form.Control
                      size='sm'
                      type="text"
                      placeholder="Cidade dos Anjos"
                      value={cidade}
                      onChange={text => setCidade(text.target.value)}
                      disabled={enderecoSetadoByCep.cidade}
                    />
                    <Form.Label>Estado / UF</Form.Label>
                    <Form.Control
                      size='sm'
                      type="text"
                      placeholder="BA"
                      value={estado}
                      onChange={text => setEstado(text.target.value)}
                      disabled={enderecoSetadoByCep.estado}
                    />
                  </Form.Group>
                </Form>
              </Col>
              
            </Row>
          </Container>
        </Modal.Body>
        <Modal.Footer>
          <Container>
            <Row>
              <Col className='d-flex justify-content-center'>
                <Button variant='success'>
                  Confirmar endereço e iniciar pagamento
                </Button>
              </Col>
            </Row>
          </Container>
        </Modal.Footer>
      </Modal>
  </>);
}
