import react, { useState, useEffect } from 'react';
import Header from '../header';
import { store, show, index, change } from '../../store/actions/items.action';
import { useSelector, useDispatch } from "react-redux";
import { CircularProgress, TextField } from '@material-ui/core';

export default function ItemEdit(props) {

  return (
    <>
      <Header/>

      <div className="container mt-4 pt-3">
        {(state.isLoading) ?
          <div className="d-flex jultify-content-center mt-5 pt-5">
            <CircularProgress />
          </div>
          :
          <div className="row">
            <div className="col-md-7">
              <h3 className="font-wheight-normal mb-4">Nome do item</h3>

              <div className="card card-body">
                <div className="row">
                  <label>Descrição</label>
                  <TextField
                    type="text"
                    onChange={ text=>{
                      dispatch( change({nome: text.target.value}) );
                    }}
                  />
                </div>
              </div>
            </div>
            <div className='col-md-5'></div>

          </div>
        }
      </div>
    </>
  )
}
