import { actionTypes } from "../actions/me.action";

const initialState = {
  NAME: '',
  EMAIL: '',
  ID: '',
  LOADING: true
}

export default (state = initialState, {type, payload}) => {
  switch (type) {
    
    case 'USER_DATA':
      return {ITEMS: payload.nome, EMAIL: payload.email, ID: payload.id, LOADING: false};

    default:
      return state;
  }
}
