import { HttpAuth } from '../../config/Http';

function payment(){

  if ((typeof localStorage.getItem('storeCart') !== 'undefined')){
    
    let itemId = [];

    function addItem(id){
      itemId.push(id);
    }

    [2, 5, 9].forEach(addItem);
    
  }

  HttpAuth.post('checkout',{ items: localStorage.getItem('storeCart') });
}

export default function Payment(){
  return (
    <>
      {payment()}
    </>
  )
}
