import React, { useState, useEffect } from 'react';
import { CircularProgress } from '@material-ui/core';
import { useSelector, useDispatch } from 'react-redux';
import { index, photo } from '../../store/actions/items.action';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import { get_cart, set_active } from '../../store/actions/cart.action';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

export default function Cart(props){

  //CART REDUCER

  const cart = useSelector(state => state.Cart);
  const dispatch = useDispatch();

  const indexCall = ()=>{
    dispatch( get_cart() );
  }

  useEffect(()=>{
    indexCall();
    index();
  },[])

  //!CART REDUCER
  const classes = useStyles();
  const [open, setOpen] = useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    if (cart.ACTIVE === true) {
      dispatch( set_active() );
    }
  };

  useEffect(()=>{
    if (cart.ACTIVE === true){
      handleOpen();
    } else if (cart.ACTIVE === false){
      handleClose();
    }
  },[cart.ACTIVE]);

  console.log(cart.ACTIVE);

  //CARREGANDO PHOTO

  useEffect (() => {
    if (!items.LOADING){
      for(let i = 0; i < items.ITEMS.length; i = i + 1 ) {
        dispatch( photo( items.ITEMS[i].id ) )
      }
    }
  }, [items.LOADING]);

  return (
    <div>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            <h2 id="transition-modal-title">Carrinho</h2>
            {cart.ITEMS.map( (item) => <>
              ID: {item.id} x QTD: {item.qtd}<br/>
              <div className='row d-flex flex-row justify-content-center'>
                {item.LOADING_PHOTO ? <CircularProgress />
                :
                  <>{item.PHOTO.map( (photo) => <>{photo.id !== item.id ? ''
                    :
                    <img className='p-2 foto' src={'http://api-lojabim.test/'.concat([photo.img])}></img>
                  }</>)}</>
                }
              </div>
            </>)}
          </div>
        </Fade>
      </Modal>
    </div>
  );
}
