import { Link } from "react-router-dom";
import style from "./style.css";
import PersonIcon from '@material-ui/icons/Person';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import { index } from '../../store/actions/me.action';
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from 'react';
import { CircularProgress } from '@material-ui/core';
import Cart from '../cart';
import { set_active } from '../../store/actions/cart.action';
import Auth from '../auth';

export default function Header() {

  const me = useSelector (state => state.Me);
  const cart = useSelector(state => state.Cart);
  const dispatch = useDispatch();

  const indexCall = () => {
    dispatch( index() );

  }

  useEffect( () => {
    indexCall();
  }, []);

  //ABERTURA E FECHAMENTO DO LOGIN
  const [opened, setOpened] = useState(false);

  return (
    <>
      <Cart />
      <Auth opened = {opened} />
      {(window.innerWidth < 577) ?
        <></>
        :
        <div className='container-fluid navbar'>
          <header className='row navbar'>
            <div className='col-8 d-flex logo'>
              [LOGO IMG]
            </div>
            <div className='col-4 row'>
              <div className='col-6 d-flex justify-content-center pt-1'>
              {(me.LOADING) ?
                  <div className="d-flex jultify-content-center mt-5 pt-5">
                    <CircularProgress />
                  </div>
                  :
                  <>{(me.NAME) ?
                      <>Ol√°, {me.NAME}</>                  
                      : 
                      <div className='loginButton' onClick={
                        (e) => { 
                          if (opened === true) {
                            setOpened(false);
                            document.getElementsByClassName(".loginButton").click();
                          } else if (opened === false){
                            setOpened(true);
                            
                          }
                        }
                      }>
                        <PersonIcon fontSize='default'/>
                        Login
                        
                      </div>
                    }</>
              }
                
              </div>
              <div className='col-6 d-flex justify-content-center pt-1 cartButton' onClick={(e)=>{
                dispatch( set_active() );
              }}>
                <ShoppingCartIcon fontSize='default'/> Carrinho
              </div>
            </div>
          </header>
        </div>  
        }
    </>
  )
}
