import react, { useState, useEffect } from 'react';
import { Typography, TextField, Button } from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { changeAuth, loginAuth } from '../../store/actions/auth.action';
import { Link, Redirect } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import style from './style.css';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

class Auth extends React.Component{
  render(){
  const classes = useStyles();
  const [open, setOpen] = useState(false);

  const handleClose = () => {
    setOpen(false)
  }

  console.log(this.props);

  useEffect( ()=>{
    setOpen(props.opened);
  },[props.opened] )

  const dispatch = useDispatch();
  const { credentials, success } = useSelector(state => state.Auth)
  
  return(
    <div>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            <div className='container-fluid'>
              <div classame='row'>
                <div 
                  className='col-12 d-flex justify-content-center login-container' id="transition-modal-title">
                    <h2 className='d-flex justify-content-center'>
                      Faça o login
                    </h2>

                    <TextField
                      label='Email'
                      type='email'
                      autoComplete='email'
                      margin='normal'
                      value={credentials.email}
                      onChange={text => dispatch(changeAuth({email: text.target.value}))}
                    />
                    
                    <TextField
                      label='Senha'
                      type='password'
                      margin='normal'
                      value={credentials.password}
                      onChange={text => dispatch(changeAuth({password: text.target.value}))}
                    />

                    <Button
                      variant='contained'
                      color='primary'
                      fullWidth
                      size='large'
                      className='mt-4 mb-4'
                      onClick={ () => dispatch( loginAuth(credentials) ) }
                    >
                      Logar
                    </Button>

                    <div className='text-center'>
                      <span className='cad-button'>
                        Não tem conta? Cadastre-se!
                      </span>
                    </div>
                  </div>
              </div>
            </div>
          </div>
        </Fade>
      </Modal>
    </div>
    
  )}}
