import React, { useState, useEffect } from 'react';
import { CircularProgress } from '@material-ui/core';
import { useSelector, useDispatch } from 'react-redux';
import { photo } from '../../store/actions/items.action';
import { makeStyles } from '@material-ui/core/styles';
import { Modal, Container, Row, Col } from 'react-bootstrap';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import { get_cart, set_active } from '../../store/actions/cart.action';
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import { style } from './style.css'; 
import { remove_items, add_items } from '../../store/actions/cart.action';
import { HttpAuth } from '../../config/Http';
import { changeNotify } from '../../store/actions/notify.action';
import { index } from '../../store/actions/me.action';
import ShowPhotos from '../showPhotos';


const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    overflow: 'auto',
    maxHeight: '80%',
    paddingTop: '34%',
    paddingBottom: '1%',
    marginTop: '5%'
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

export default function Cart(props){

  const me = useSelector (state => state.Me);
  const items = useSelector(state => state.Items);

  const [cartButtonOff, setCartButtonOff] = useState(false);

  //TOTAL DO CARRINHO
  let total = 0;
  const adicionarTotal = (preco, qtd) => {
    total = total + (parseFloat(preco) * parseFloat(qtd));
  }
  const convertTotal = (num) => {
    return num.toLocaleString(
      'pt-BR', { style: 'currency', currency: 'BRL' }
    );
  }
  //!TOTAL DO CARRINHO

  //CART REDUCER
  const cart = useSelector(state => state.Cart);
  const dispatch = useDispatch();

  const indexCall = ()=>{
    dispatch( get_cart() );
  };

  useEffect(()=>{
    indexCall();
  },[]);
  //!CART REDUCER

  const classes = useStyles();

  //Mecanismo de abertura e fechamento do carrinho
  const [open, setOpen] = useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    if (cart.ACTIVE === true) {
      dispatch( set_active(false) );
    }
  };

  useEffect(()=>{
    if (cart.ACTIVE === true){
      handleOpen();
    } else if (cart.ACTIVE === false){
      handleClose();
    }
  },[cart.ACTIVE]);

  //Payment call (pagseguro)
  function payment(){

    let cart = localStorage.getItem('storeCart');

    if (me.NAME) {

      if ((typeof cart !== 'undefined' || cart !== '' || cart !== [])){

        setCartButtonOff(true);
        
        let itemsIdQtd = [];

        //LOGICA DE ADICINAR OS IDS DO CARRINHO
        for (let i = 0; i < JSON.parse(cart).length; i++){
    
          itemsIdQtd = [...itemsIdQtd, {
            itemId: JSON.parse(cart)[i].id,
            itemQuantity: JSON.parse(cart)[i].qtd,
            itemTam: JSON.parse(cart)[i].tamanho
          }];
    
        }

        localStorage.removeItem('storeCart');

        HttpAuth.post('checkout', {items: JSON.stringify(itemsIdQtd)}).then(
          (response) => {
            window.location = response.data;
            ;
          }
        );

      } else {

        dispatch( changeNotify({
          open: true,
          class: 'error',
          msg: 'Seu carrinho está vazio.'
        }));

      }
    } else {

      dispatch( changeNotify({
        open: true,
        class: 'error',
        msg: 'Faça o login para concluir a sua compra.'
      }));

    }
  }

  //Função que verifica se o item é o último listado. Caso não seja adiciona um separador.
  let indexAtual = 0;

  function separador(indexTotal){
    indexAtual++;
    if (indexAtual < indexTotal){
      return (<hr />);
    }
  }

  return (
      <Modal show={open} onHide={handleClose} size='xl'>
        <Modal.Header closeButton>
          <h2>
            Carrinho
          </h2>
        </Modal.Header>
        <Modal.Body>
          <Row>
            <Col>
              {cart.ITEMS.map( (item) =>
                <div key={item.id}>
                  <Row>
                    <div className='col-4'>
                      {
                        items.LOADING_PHOTO ? <CircularProgress/> : <ShowPhotos id={item.id}/>
                      }
                    </div>
                    <div className='col-8'>
                      <div className='namePrice-container'>
                        <div className='name-container'>
                          <h3>
                            {item.nome}
                          </h3>
                        </div>
                        <div className='name-container'>
                          <small>
                            Tamanho: {item.tamanho}
                          </small>
                        </div>
                        <div className='priceQtd-container'>
                          <div>
                            <span>
                              <AddIcon
                              className='addButton'
                              onClick={ (e) => dispatch( add_items(item.id, item.qtd, item.img, item.desc, item.preco, item.tamanho) ) }
                              />
                              {item.qtd}
                              <RemoveIcon
                                className='removeButton'
                                onClick={ (e) => dispatch( remove_items(item.id, item.tamanho) ) }
                              />
                            </span>
                          </div>
                          <span className='itemPreco'>R$ {item.preco}</span>
                        </div>
                      </div>
                    </div>
                  </Row>
                  {adicionarTotal(item.preco, item.qtd)}
                  {
                    //Função que verifica se é o último item listado. Se não for, adiciona o separador.
                    separador(cart.ITEMS.length)
                  }
                </div>
              )}
              {total === 0 && 
                <div className='row d-flex'>
                  <div className='col-12 d-flex justify-content-center'>
                    <h6>
                      Carrinho vazio
                    </h6>
                  </div>
                </div>
              }
            </Col>
          </Row>
        </Modal.Body>
        <Modal.Footer>
          {total !== 0 &&
            <div className='row'>
              <span className='col-4 d-flex justify-content-center'>
                <h4>
                  Total
                </h4>
              </span>
              <span className='col-4 d-flex justify-content-center'>
                <h4>
                  {convertTotal(total)}
                </h4>
              </span>
              <span className='col-4 d-flex justify-content-center'>
                <button
                onClick={ (e) => {payment()} }
                disabled = { cartButtonOff && true}
                >
                  Finalizar compra
                </button>
              </span>
            </div>
          }
        </Modal.Footer>
      </Modal>
  );
}
