import react, { useState, useEffect } from 'react';
import Header from '../header';
import { indexResponse, index } from '../../store/actions/items.action';
import { useSelector, useDispatch } from "react-redux";
import { CircularProgress, ListItemSecondaryAction, TextField } from '@material-ui/core';

export default function ItemEdit(props) {

  const items = useSelector (state => state.Items.items);
  const [isLoading, setLoading] = useState(true);
  const [isLoadMore, setLoadMore] = useState(false);
  const [query, setQuery] = useState('');

  const dispatch = useDispatch();

  useEffect( ()=>{
    setLoadMore(true);
    indexCall();
    console.log(isLoadMore)
  }, []);

  const [state, setState] = useState({
    isLoading: true,
    isDeleted: null,
    redirect: false,
    tips: 0,
    confirmElement: null,
    id: (props.match.params.id) ? props.match.params.id : null,
  });

  const indexCall = () => {
    dispatch( index(query, true) ).then(res => {
      if (res){
        setLoading(false);
        setLoadMore(false);
        if(isLoadMore && setLoadMore(false));
        setState ( {isLoading: false} );
      }
    })
  }

  return (
    <>
      <Header/>
      {(state.isLoading) ?
          <div className="d-flex jultify-content-center mt-5 pt-5">
            <CircularProgress />
          </div>
          :
          <>{console.log(items)}</>
      }
    </>
  )
}
