import react, { useState } from 'react';
import Header from '../header';
import { index } from '../../store/actions/items.action';
import { store, show } from '../../actions/items.action;'
import { useSelector, useDispatch } from "react-redux";
import { CircularProgress } from '@material-ui/core';

export default function ItemEdit(props) {

  const [state, setState] = useState({
    isLoading: true,
    isDeleted: null,
    redirect: false,
    tips: 0,
    confirmElement: null,
    item_id: (props.match.params.id) ? props.match.params.id : null
  });

  const dispatch = useDispatch();
  const data = useSelector(state => state.Items.item)

  return (
    <>
      <Header/>
      <div className="container mt-4 pt-3">
        {(state.isLoading) ?
          <div className="d-flex jultify-content-center mt-5 pt-5">
            <CircularProgress />
          </div>
          :
          <div>
          </div>
        }
      </div>
    </>
  )
}
