import React, { useState, useEffect } from 'react';
import { TextField, Button } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
import { changeRegister, register } from '../../store/actions/register.action';
import { Link, Redirect } from 'react-router-dom';
import { Modal, Container, Row, Col } from 'react-bootstrap';
import { style } from './style.css';

export default function Register(props) {

  const dispatch = useDispatch();
  const { user, error, success } = useSelector(state => state.Register);

  //Abertura e fechamento do modal
  const [open, setOpen] = useState();

  useEffect( ()=>{

    if (props.open === undefined){
      //faz nada kkk
    } else if (props.open === true) {
      setOpen(true);
    }
  },[props.open] );

  const handleClose = () => {
    setOpen(false);
  }

  return (
    <Modal show={open} onHide={handleClose} size='xl'>
      <Modal.Header closeButton>
        <h2>
          Inscreva-se
        </h2>
      </Modal.Header>
      <Modal.Body>
        <Container>
          <Row>
            <Col>
            {console.log( error )}
              <TextField
                
                error = {(error.name) && true}
                margin = 'normal'
                label = 'Nome Completo'
                value = {user.name}
                onChange = {text => {
                  dispatch( changeRegister({ name: text.target.value }));
                  if (error.name && delete error.name);
                }}
                fullWidth
              />
              {(error.name) && 
                <strong className='text-danger'>{error.name[0]}</strong>
              }
            </Col>
            <Col>
              <TextField
                error = {(error.cpf) && true}
                margin = 'normal'
                label = 'Cpf'
                value = {user.cpf}
                onChange = {text => {
                  dispatch( changeRegister({ cpf: text.target.value }));
                  if (error.cpf && delete error.cpf);
                }}
                fullWidth
              />
              {(error.cpf) && 
                <strong className='text-danger'>{error.cpf[0]}</strong>
              }
            </Col>
          </Row>
          <Row>
            <Col>
              <TextField
                error = {(error.email) && true}
                margin = 'normal'
                label = 'Email'
                value = {user.email}
                type = 'email'
                autoComplete = 'email'
                onChange = {text => {
                  dispatch( changeRegister({ email: text.target.value }));
                  if (error.email && delete error.email);
                }}
                fullWidth
              />
              {(error.email) && 
                <strong className='text-danger'>{error.email[0]}</strong>
              }
            </Col>
            <Col>
              <TextField
                error = {(error.tel) && true}
                margin = 'normal'
                label = 'Telefone'
                value = {user.tel}
                type = 'text'
                onChange = {text => {
                  dispatch( changeRegister({ tel: text.target.value }));
                  if (error.tel && delete error.tel);
                }}
                fullWidth
              />
              {(error.telefone) && 
                <strong className='text-danger'>{error.telefone[0]}</strong>
              }
            </Col>
          </Row>
          <Row>
            <Col>
              <TextField
                error = {(error.password) && true}
                margin = 'normal'
                label = 'Senha'
                type = 'password'
                value = {user.password}
                onChange = {text => {
                  dispatch( changeRegister({ password: text.target.value }));
                  if (error.password && delete error.password);
                }}
                fullWidth
              />
              {(error.password) && 
                <strong className='text-danger'>{error.password[0]}</strong>
              }
            </Col>
            <Col>
              <TextField
                error = {(error.conf_password) && true}
                margin = 'normal'
                label = 'Confirme a Senha'
                type = 'password'
                value = {user.conf_password}
                onChange = {text => {
                  dispatch( changeRegister({ conf_password: text.target.value }));
                  if (error.conf_password && delete error.conf_password);
                }}
                fullWidth
              />
              {(error.conf_password) && 
                <strong className='text-danger'>{error.conf_password[0]}</strong>
              }
            </Col>
          </Row>
          <Row>
            <Col>
              <Button
                variant = 'contained'
                color = 'primary'
                fullWidth
                size = 'large'
                className = 'mt-4 mb-4'
                onClick = {() => dispatch( register(user) )}
              >
               Cadastrar
              </Button>
            </Col>
          </Row>
          <Row>
            <Col>
              <div
                className='text-center logue-se'
                onClick={(e)=>{handleClose()}}
              >
                Já tem conta? Faça o Login
              </div>
              {(success) &&
                <Redirect to='/' />
              }
            </Col>
          </Row>
        </Container>
      </Modal.Body>
    </Modal>
  )
}
