import { actionTypes } from '../actions/cart.action';

const initialState = {
  ITEMS: [],
  ACTIVE: false
}

export default (state = initialState, {type, payload}) => {
  switch (type){

    case actionTypes.ADD_ITEM:
      let arr_items = state.ITEMS;
      let items = state.ITEMS.findIndex( item => item.id == payload.id );

      if(items < 0) {
        arr_items.push({id: payload.id, qtd: payload.qtd});
      } else {
        arr_items.push({id: payload.id, qtd: state.ITEMS[items].qtd + 1});
        arr_items.splice(items, 1);
      }

       return {...state, ITEMS:  arr_items}
    

    case actionTypes.REMOVE_ITEM:
      state.ITEMS.map( (item) => {
        if (item.id === payload.id){
          return false;
        } else {
          return true;
        }
      })

    case actionTypes.SET_CART:
      localStorage.removeItem('storeCart');
      localStorage.setItem('storeCart', JSON.stringify(state.ITEMS));

    case actionTypes.GET_CART:
      let cart = localStorage.getItem('storeCart');
      if (cart !== null) {
        return {...state, ITEMS: JSON.parse(cart)}
      }

    case actionTypes.ACTIVE:
      if (state.ACTIVE === false) {
        let isActive = true;
      } else if (state.ACTIVE === false) {
        let isActive = false;
      }
      return {...state, ACTIVE: isActive}
      

    default:
      return state
  }
}
