import react, { useState, useEffect } from 'react';
import Header from '../header';
import { store, show, index, change, indexResponse } from '../../store/actions/items.action';
import { useSelector, useDispatch } from "react-redux";
import { CircularProgress, ListItemSecondaryAction, TextField } from '@material-ui/core';
import { scroll } from '../../config/App';

export default function ItemEdit(props) {

  const [isLoading, setIsLoading] = useState(true);
  const [isLoadingMore, setIsLoadingMore] = useState(true);
  const [query, setQuery] = useState(true);

  const [state, setState] = useState({
    isDeleted: null,
    confirmElement: null,
    id: (props.match.params.id) ? props.match.params.id : null,
    response: undefined
  });

  const dispatch = useDispatch();

  useEffect( ()=>{
    document.addEventListener('scroll', handleScroll);
    indexCall();
  }, []);

  const handleScroll = (e) => {
    let scrollTop = e.srcElement.body.scrollHeight - (e.srcElement.body.offsetHeight + e.srcElement.body.scrollTop)
    if (scrollTop < scroll){
      if(!isLoadMore && handleLoadMore());
    }
  }

  const handleLoadMore = () => {
    if (item.current_page < item.last_page) {
      setQuery({
        ...query,
        page: qyery.page + 1
      }, ()=>{
        index(true);
      })
    }
  }

  const indexCall = (LoadMore) => {
    dispatch( index(query, LoadMore) ).then(res => {
      if(res){
        setState ( {isLoading: false, response: res} );
        if(isLoadMore && setIsLoadingMore(false));
      }
    })
  }


  return (
    <>
      <Header/>
      {(state.isLoading) ?
          <div className="d-flex jultify-content-center mt-5 pt-5">
            <CircularProgress />
          </div>
          :
          <>{}</>
      }
    </>
  )
}
