import react, { useState, useEffect } from 'react';
import Header from '../header';
import { index } from '../../store/actions/items.action';
import { useSelector, useDispatch } from "react-redux";
import { CircularProgress, ListItemSecondaryAction, TextField } from '@material-ui/core';
import itemsReducer from '../../store/reducers/items.reducer';

export default function ItemEdit(props) {

  const items = useSelector (state => state.Items);
  const [isLoading, setLoading] = useState(true);
  const [lista, setLista] = useState('');
  const dispatch = useDispatch();

  async function indexCall () {
    await dispatch( index() )
    await setLista(items);
    await setLoading(false);
  }

  useEffect( ()=>{
    indexCall();
  }, [])

  return (
    <>
      <Header/>
      {(isLoading) ?
          <div className="d-flex jultify-content-center mt-5 pt-5">
            <CircularProgress />
          </div>
          :
          <>{console.log(lista)}</>
      }
    </>
  )
}
