import react from 'react';

const SignUpApi = () => {

  const [name, setName] = react.useState('');
  const [email, setEmail] = react.useState('');
  const [password, setPassword] = react.useState('');

  function handleSubmit(event){
    event.preventDefault();
    fetch('http://api-lojabim.test/api/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'X-Requested-With': 'XMLHttpRequest'
      },
      body: JSON.stringify({
        name,
        email,
        password
      })
    }).then((response) => {
      return response.json();
    }).then((json)=>{
      return json;
    });
  }

  return(
    <form onSubmit={handleSubmit}>
      <input type='text' value={name} onChange={({target}) => setName(target.value)} placeholder='name'/>

      <input type='text' value={email} onChange={({target}) => setEmail(target.value)} placeholder='email'/>

      <input type='text' value={password} onChange={({target}) => setPassword(target.value)} placeholder='password'/>

      <button>Enviar</button>
    </form>
  )
}

export default SignUpApi;
