import { actionTypes } from '../actions/loading.action';

const initialState = {
  open: true,
  msg: 'Loading...'
}

export default function Loading (state = initialState, {type, data}) {
  switch (type){
    case actionTypes.CHANGE:
      return {...state, ...data}

      default:
        return state
  }
}
