import { actionTypes } from '../actions/cart.action';

const initialState = {
  ITEMS: [
    {
      id: '',
      qtd: '',
    }
  ]
}

export default (state = initialState, {type, payload}) => {
  switch (type){

    case actionTypes.ADD_ITEM:
      return {...state, payload}

    case actionTypes.REMOVE_ITEM:
      ITEMS.map( (item) => {
        if (item.id === payload.id){
          return false;
        } else {
          return true;
        }
      })

    default:
      return state
  }
}
