import React from 'react';
import styled from 'styled-components';
import PersonIcon from '@material-ui/icons/Person';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import capa01 from './public/banners/capa01.jpeg';

const Header = styled.div`
  font-size: 1.0em;
  color: white;
  background-color: black;
  height: 10vh;
`;

const Logo = styled.div`
  font-size: 1.5em;
  padding-left: 15%;
`;

const Section1 = styled.div`
  height: 60vh;
  width: 100%;
`;

const BannerSection1 = styled.div`
  height: 100%;
  width: 100%;
  background-image: url(${capa01});
  background-size: cover;
`;

const BannerRow = styled.div`
  height: 100%;
  width: 100%;
`;

const Welcome = ()=>(
  <>
    <Header className='container-fluid'>
      <header className='row pt-3'>
        <Logo className='col-8 d-flex'>
          Loja do bim
        </Logo>
        <div className='col-4 row'>
          <div className='col-6 d-flex justify-content-center pt-1'>
            <PersonIcon fontSize='default'/>
          </div>
          <div className='col-6 d-flex justify-content-center pt-1'>
            <ShoppingCartIcon fontSize='default'/>
          </div>
        </div>
      </header>
    </Header>
    <Section1 className='container'>
      <BannerRow className='row'>
        <div className='col-md-6 d-flex justify-content-center'>
          Compre Aqui
        </div>
        <div className='col-md-6'>
          <BannerSection1 />
        </div>
      </BannerRow>
    </Section1>
  </>
)

export default Welcome;
