import react, { useState, useEffect } from 'react';
import { Typography, TextField, Button } from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { changeAuth, loginAuth } from '../../store/actions/auth.action';
import { Link, Redirect } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

export default function Auth(props){

  const classes = useStyles();
  const [open, setOpen] = useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  useEffect( ()=>{
    setOpen(props.opened);
    console.log(open)
    if (open) {
      handleOpen()
    } else {
      handleClose()
    }
    
  },[props.opened] )

  const dispatch = useDispatch();
  const { credentials, success } = useSelector(state => state.Auth)
  
  return(
    <div>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            <div className='container-fluid'>
              <div classame='row'>
                <h2 
                  className='col-12 d-flex justify-content-center' id="transition-modal-title">
                    <Typography className='mt-3' variant='h6' component='h1'>
                      Se autentique
                    </Typography>

                    <TextField
                      label='Email'
                      type='email'
                      autoComplete='email'
                      margin='normal'
                      value={credentials.email}
                      onChange={text => dispatch(changeAuth({email: text.target.value}))}
                    />
                    
                    <TextField
                      label='Senha'
                      type='password'
                      margin='normal'
                      value={credentials.password}
                      onChange={text => dispatch(changeAuth({password: text.target.value}))}
                    />

                    <Button
                      variant='contained'
                      color='primary'
                      fullWidth
                      size='large'
                      className='mt-4 mb-4'
                      onClick={ () => dispatch( loginAuth(credentials) ) }
                    >
                      Logar
                    </Button>

                    <div className='text-center'>
                      <Link to='/login' className='mt-4 text-success'>
                        Fazer Login
                      </Link>
                    </div>
                    </h2>
              </div>
              <div clasName='row'>
                
              </div>
            </div>
          </div>
        </Fade>
      </Modal>
    </div>
  )}
