import useState from 'react';
import Modal from '@material-ui/core/Modal';

export default function openedItems(props) {
  return(
    <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
      <div className={'row d-flex align-items-center '.concat(classes.paper)}>
        <div className='col-md-6 d-flex align-items-center' height='100%'>
          <ShowPhotos id={item.id}/>
        </div>
        <div className='col-md-6 d-flex flex-column align-items-center justify-content-around item-display'>
          <h2>
            {item.desc}
          </h2>
          <div>
            {tamanhoSel(item.tipo)}
          </div>
          <div>
            <h3>
              R$ {item.preco}
            </h3>
          </div>
          <button
          onClick={()=>{
            if (tamanho === undefined){
              dispatch( changeNotify({
                open: true,
                class: 'error',
                msg: 'Selecione um tamanho.'
              }));
            } else {
              dispatch( add_items(item.id, 1, item.foto, item.desc, item.preco, tamanho));
              setTamanho(undefined);
              handleClose();
            }
          }}
          >
            Adicionar ao carrinho
          </button>
        </div>
      </div>
    </Modal>
  );
}
