import { HttpAuth } from '../../config/Http';

export const actionTypes = {
  CHANGE: 'REGISTER_CHANGE',
  ERROR: 'REGISTER_ERROR',
  SUCCESS: 'REGISTER_SUCCESS'
}

export const index = () => dispatch => {
  HttpAuth.get('/item')
    .then( (response) => {
      dispatch({
        type: 'INDEX_ITEMS',
        payload: response.data
      })
    })
}
