function IsAuthenticated(){

  const isLogged = () => {

    //Recuperação de token de usuário no local storage
    const access_token = localStorage.getItem('access_token');

    if (access_token){

      //Fetch para recuperação do usuário via token
      fetch('http://api-lojabim.test/api/me', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'X-Requested-With': 'XMLHttpRequest',
          'Authorization': 'Bearer ' + access_token
        }
      }).then((response) => {
        return response.json();
      }).then((json)=>{
        console.log(json);
        if (json.error){
          alert('usuário não encontrado');
        } else {
          console.log(json);
        }
      });

    } else {

      //Se não houver token no local storage, retorne falso
      return false;
    }
  }

  return (
    <>
      {isLogged()}
    </>
  );
}

export {IsAuthenticated};
