import React,{ useState, useEffect } from 'react';
import { Modal, Row, Button, Form } from 'react-bootstrap';
import { HttpAuth } from '../../../config/Http';

export default function Endereco(props) {

  //Mecanismo de abertura e fechamento do modal
  const [open, setOpen] = useState(false);
  const [qtdEndereco, setQtdEndereco] = useState(0);
  const [endereco, setEndereco] = useState({
    rua: '',
    numero: '',
    complemento: '',
    bairro: '',
    cep: '',
    cidade: '',
    estado: '',
    pais: 'BRA'
  });

  const handleClose = () => {
    setOpen(false);
  };

  useEffect(()=>{
    if (props.open){
      setOpen(true);
      buscaEndereco();
    }
  },[props.open]);

  function buscaEndereco (){
    HttpAuth.get('cliente/'+props.id+'/endereco').then(
      (response) => {
        if (response.data !== undefined) {
          setQtdEndereco(response.data.length);
          console.log(response.data[qtdEndereco-1]);
        } else {}
      }
    );
  }

  return (<>
    <Modal show={open} onHide={handleClose} size='xl'>
        <Modal.Header closeButton>
          <h2>
            Endereço de entrega
          </h2>
        </Modal.Header>
        <Modal.Body>
          <Row>
            <Form>
              <Form.Group controlId="endereco">
                <Form.Label>Cep</Form.Label>
                <Form.Control size='sm' type="text" placeholder="12345-123" />
                <Form.Text className="text-muted">
                  Caso não saiba seu cep: 
                  <a href='https://buscacepinter.correios.com.br/app/endereco/index.php'>
                    Pesquise seu cep
                  </a>
                </Form.Text>
                <Form.Label>Rua</Form.Label>
                <Form.Control size='sm' type="text" placeholder="Rua ciclano da silva" />
                <Form.Label>numero</Form.Label>
                <Form.Control size='sm' type="number" placeholder="10" />
                <Form.Check 
                  type="switch"
                  id="isSemNumero"
                  label="Check this switch"
                />
                <Form.Label>complemento</Form.Label>
                <Form.Control size='sm' type="text" placeholder="Ap 1001" />
                
              </Form.Group>
            </Form>
          </Row>
        </Modal.Body>
        <Modal.Footer>
          
        </Modal.Footer>
      </Modal>
  </>);
}
