import React from 'react';
import styled from 'styled-components';
import PersonIcon from '@material-ui/icons/Person';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import capa01 from './public/banners/capa01.jpeg';
import BannerItems from './view/components/BannerItems';

const Header = styled.div`
  font-size: 1.0em;
  color: white;
  background-color: #474a51;
  height: 10vh;
`;

const Logo = styled.div`
  font-size: 1.5em;
  padding-left: 15%;
`;

const Section1 = styled.div`
  min-height: 60vh;
  width: 100%;
`;

const BannerSection1 = styled.div`
  min-height: 60vh;
  width: 100%;
  background-image: url(${capa01});
  background-size: cover;
`;

const BannerRow = styled.div`
  height: 100%;
  width: 100%;
  font-size: 3.0em;
`;

const Categories = styled.div`
  min-height: 6vh;
  background-color: #474a51;
  color: white;
`;

const Section2 = styled.div`
  min-height: 124vh;
  width: 100%;
  background-color: #474a51;
`;

const Items = styled.div`
  height: 100%;
`;

const Welcome = ()=>(
  <>
    <Header className='container-fluid'>
      <header className='row pt-3'>
        <Logo className='col-8 d-flex'>
          [LOGO IMG]
        </Logo>
        <div className='col-4 row'>
          <div className='col-6 d-flex justify-content-center pt-1'>
            <PersonIcon fontSize='default'/>
          </div>
          <div className='col-6 d-flex justify-content-center pt-1'>
            <ShoppingCartIcon fontSize='default'/>
          </div>
        </div>
      </header>
    </Header>
    <Section1 className='container'>
      <BannerRow className='row'>
        <div className='col-md-6 d-flex justify-content-center' style={{alignItems: 'center'}}>
          Compre Aqui
          <br/>
          [Example text]
        </div>
        <div className='col-md-6'>
          <BannerSection1 />
        </div>
      </BannerRow>
    </Section1>
    <Categories className='container-fluid'>
      <div className='row'>
        <div className='col-4 d-flex justify-content-center pt-2 pb-2'>
          Camisas
        </div>
        <div className='col-4 d-flex justify-content-center pt-2 pb-2'>
          Calças & Bermudas
        </div>
        <div className='col-4 d-flex justify-content-center pt-2 pb-2'>
          Calçados
        </div>
      </div>
    </Categories>
    <Section2>
      <BannerItems>
      </BannerItems>
    </Section2>
  </>
)

export default Welcome;
