import react from 'react';
import { Typography, TextField, Button } from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { changeAuth, loginAuth } from '../../store/actions/auth.action';
import { Redirect } from 'react-router-dom';

export default function Auth(){
  const dispatch = useDispatch();
  const { credentials, success } = useSelector(state => state.Auth)
  return(
    <div className='d-flex bg-white min-vh-100'>
      <div className='container mt-5'>
        <div className='row justify-content-center'>
          <div className='col-md-4'>
            <div className='form-group text-center'>
              <Typography className='mt-3' variant='h6' component='h1'>
                Se autentique
              </Typography>

              <TextField
                label='Email'
                type='email'
                autoComplete='email'
                margin='normal'
                value={credentials.email}
                onChange={text => dispatch(changeAuth({email: text.target.value}))}
              />

              <TextField
                label='Senha'
                type='password'
                margin='normal'
                value={credentials.password}
                onChange={text => dispatch(changeAuth({password: text.target.value}))}
              />

              <Button
                variant='contained'
                color='primary'
                fullWidth
                size='large'
                className='mt-4 mb-4'
                onClick={ () => dispatch( loginAuth(credentials) ) }
              >
                Logar
              </Button>

              {
                (success) &&
                <Redirect to='/'/>
              }
            </div>
          </div> 
        </div>
      </div>
    </div>
  )
}
