import react, { useState, useEffect } from 'react';
import Header from '../header';
import { index, store, show } from '../../store/actions/items.action';
import { useSelector, useDispatch } from "react-redux";
import { CircularProgress } from '@material-ui/core';

export default function ItemEdit(props) {

  useEffect( ()=>{
    index();
  }, [] );

  const [state, setState] = useState({
    isLoading: true,
    isDeleted: null,
    redirect: false,
    tips: 0,
    confirmElement: null,
    item_id: (props.match.params.id) ? props.match.params.id : null
  });

  const dispatch = useDispatch();
  const data = useSelector(state => state.Items.item);

  const index = () => {
    if(state.item_id){
      dispatch( show(state.item_id) ).then( response => {
        if(response) {
          setState({ isLoading: false });
        } else {
          dispatch( store().then(response => {
            setState( {isLoading: false} );
          }));
        }
      } )
    }
  }

  return (
    <>
      <Header/>
      <div className="container mt-4 pt-3">
        {(state.isLoading) ?
          <div className="d-flex jultify-content-center mt-5 pt-5">
            <CircularProgress />
          </div>
          :
          <div>
          </div>
        }
      </div>
    </>
  )
}
