import { HttpAuth } from '../../config/Http';
import { changeLoading } from './loading.action';

export const actionTypes = {
  CHANGE: 'ITEM_CHANGE',
  INDEX: 'ITEM_INDEX',
  DESTROY: 'ITEM_UPDATE',
  SUCCESS: 'ITEM_ERROR',
  ERROR: 'ITEM ERROR'
}
export const change = (data) => ({
  type: actionTypes.CHANGE,
  data
});

export const success = (data) => ({
  type: actionTypes.SUCCESS,
  data
})

export const error = (data) => ({
  type: actionTypes.ERROR,
  data
});

export const indexResponse = (data, isLoadMore) => ({
  type: actionTypes.INDEX,
  data,
  isLoadMore
});

export const index = (query, isLoadMore) => dispatch => {
  
  return HttpAuth.get('/item' + new URLSearchParams(query))
  .then(response => {
    if (typeof response !== 'undefined') {
      dispatch( indexResponse(response.data, isLoadMore) );
    } else {
      return response.json();
    }
  })
  .catch(error => {
    return ('algo deu errado.');
  })
}

export const store = () => dispatch => {
  return HttpAuth.post('/item')
  .then(response => {
    if (typeof response !== 'undefined') {
      dispatch( indexResponse(response.data) );
    }
  })
  .catch(error => {
    return ('algo deu errado.')
  })
}

export const show = (id) => dispatch => {

  return HttpAuth.get('/item/' + id)
  .then(response => {
    if (typeof response !== 'undefined') {
      dispatch( indexResponse(response.data) );
    }
  })
  .catch(error => {
    return ('algo deu errado.')
  })
}

export const update = (data) => dispatch => {
  dispatch(changeLoading({
    open: true
  }));

  return HttpAuth.put('/item/' + data.id, data)
  .then(response => {

    dispatch(changeLoading({
      open: false
    }));

    if (typeof response !== 'undefined') {

      if(response.data.error){
        dispatch ( success(false) );
        dispatch ( error(response.data.error) );
      }

      if (response.data.status === 200){
        dispatch (success(true));
      }
      dispatch( indexResponse(response.data) );
    }
  })
  .catch(error => {
    return ('algo deu errado.')
  })
}

export const destroyResponse = (data) => ({
  type: actionTypes.DESTROY,
  data
});

export const destroy = (id) => dispatch => {
  dispatch( changeLoading({
    open: true
  }) );

  return HttpAuth.delete('/item/' + id)
  .then(response => {

    dispatch(changeLoading({
      open: false
    }));

    if (typeof response !== 'undefined') {

      if(response.data.error){
        dispatch ( success(false) );
        dispatch ( error(response.data.error) );
      }

      if (response.data.status === 200){
        dispatch ( destroyResponse(id) );
        dispatch ( success(true) );
      }
      dispatch( indexResponse(response.data) );
    }
  })
  .catch(error => {
    return ('algo deu errado.')
  })
}
