import react, { useState } from 'react';
import { Redirect } from 'react-router-dom';

const LoginApi = ()=>{
  const [email, setEmail] = react.useState('');
  const [password, setPassword] = react.useState('');

  const [redirect, setRedirect] = react.useState(false);
  useState(()=>{});
  
  //Função de submit do formulário de login
  function handleSubmit(event){

    //Prevenção de comportamento padrão
    event.preventDefault();

    //Requisição ao back-end para retornar o token correspondente ao login e senha
    fetch('http://api-lojabim.test/api/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'X-Requested-With': 'XMLHttpRequest'
      },
      body: JSON.stringify({
        email,
        password
      })
    }).then((response) => {
      return response.json();
    }).then((json)=>{
      console.log(json);
      if (json.error){
        alert('Erro no login.');
      } else {
        localStorage.setItem('access_token', json.access_token);
        setRedirect(true);
      }
    });
  }

  return(
    <form onSubmit={handleSubmit}>
      <input type='text' value={email} onChange={({target}) => setEmail(target.value)} placeholder='email'/>

      <input type='text' value={password} onChange={({target}) => setPassword(target.value)} placeholder='password'/>

      <button>Enviar</button>
    </form>
  )
}

export default LoginApi;
