import { changeNotify } from './notify.action';

export const actionTypes = {
  ADD_ITEM: 'ADD_ITEM',
  REMOVE_ITEM: 'REMOVE_ITEM',
  SET_CART: 'SET_CART',
  GET_CART: 'GET_CART',
  SET_ACTIVE: 'SET_ACTIVE'
}

export const get_cart = () => dispatch => {
  dispatch ({
    type: actionTypes.GET_CART
  });
}

const set_cart = () => dispatch => {
  dispatch ({
    type: actionTypes.SET_CART
  });
}

export const add_items = (id, qtd) => dispatch => {
  if(dispatch({
      type: actionTypes.ADD_ITEM,
      payload: {id: id, qtd: qtd}
    })
  ){
    dispatch( changeNotify({
      open: true,
      class: 'success',
      msg: 'Item inserido no carrinho com sucesso.'
    }));
    dispatch ( set_cart() );
  } else {
    dispatch( changeNotify({
      open: true,
      class: 'error',
      msg: 'O item n達o foi inseridono carrinho.'
    }));
  }
}

export const remove_items = ({id}) => dispatch => {
  if(
    dispatch ({
      type: actionTypes.REMOVE_ITEM,
      payload: {id: id}
    })
  ){
    dispatch( changeNotify({
      open: true,
      class: 'success',
      msg: 'Item removido do carrinho com sucesso.'
    }));
    dispatch ( set_cart() );
  } else {
    dispatch( changeNotify({
      open: true,
      class: 'error',
      msg: 'Item n達o removido do carrinho.'
    }));
  }
}
