import React, { useState, useEffect } from 'react';
import { CircularProgress } from '@material-ui/core';
import { useSelector, useDispatch } from 'react-redux';
import { index, photo } from '../../store/actions/items.action';
import { style } from './style.css';


export default function BannerItems(){

  const dispatch = useDispatch();
  const items = useSelector(state => state.Items);
  let id_item_photo = '';

  function indexCall() {
    dispatch( index() );
  }

  useEffect( () => {
    if (id_item_photo !== ''){
      dispatch ( photo(id_item_photo) )
    }
  }, [id_item_photo]);

 useEffect( () => {
    indexCall();
  }, []);

  return (
    <div className="container pt-5">
      <div className="row justify-content-center">
        <div className="col-auto">
          <h1>
            Ãšltimas novidades
          </h1>
        </div>
      </div>
      <div className="row justify-content-center">
        {items.LOADING ? <CircularProgress />
        :
        <>
          {items.ITEMS.map( (item) =>
            <div class='col-md-4 justify-content-center d-flex card'>
              <span class='d-flex justify-content-center title'>{item.nome}</span>
              <span class='d-flex jutify-content-center preco'>{item.preco}</span>
              <span class='d-flex jutify-content-center desc'>{item.desc}</span>
              {id_item_photo = [...id_item_photo, item.id]}
            </div>
          )}
        </>
        }
      </div>
    </div>
  )
}
