import react from "react";
import { Link } from "react-router-dom";
import style from "./style.css";
import PersonIcon from '@material-ui/icons/Person';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import { index } from '../../store/actions/me.action';

export default function Header() {

  const me = useSelector (state => state.Me);
  const [isLoading, setLoading] = useState(true);
  const dispatch = useDispatch();

  const indexCall = () => {
    dispatch( index() );
    setLoading(false);
  }

  useEffect( ()=>{
    indexCall();
  }, []);

  return (
    <>
      {(window.innerWidth < 577) ?
        <></>
        :
        <div className='container-fluid navbar'>
          <header className='row navbar'>
            <div className='col-8 d-flex logo'>
              [LOGO IMG]
            </div>
            <div className='col-4 row'>
              <div className='col-6 d-flex justify-content-center pt-1'>
              {(isLoading) ?
                  <div className="d-flex jultify-content-center mt-5 pt-5">
                    <CircularProgress />
                  </div>
                  :
                  <>{(me) ?
                      <>me.nome</>                  
                      : 
                      <><PersonIcon fontSize='default'/></>
                    }</>
              }
                
              </div>
              <div className='col-6 d-flex justify-content-center pt-1'>
                <ShoppingCartIcon fontSize='default'/>
              </div>
            </div>
          </header>
        </div>  
        }
    </>
  )
}
