import { HttpAuth } from '../../config/Http';

function payment(){

  if ((typeof localStorage.getItem('storeCart') !== 'undefined')){
    
    let itemsId = [];

    //LOGICA DE ADICINAR OS IDS DO CARRINHO

    console.log(itemsId);
    console.log(localStorage.getItem('storeCart').map( () => item.id));
    
    HttpAuth.post('checkout',{ items: itemsId });
    
  }
}

export default function Payment(){
  return (
    <>
      {payment()}
    </>
  )
}
