import React,{ useState, useEffect } from 'react';
import { Modal, Button, Form, Container, Row, Col } from 'react-bootstrap';
import { HttpAuth } from '../../../config/Http';

export default function Endereco(props) {

  //Mecanismo de abertura e fechamento do modal
  const [open, setOpen] = useState(false);
  const [qtdEndereco, setQtdEndereco] = useState(1);
  const [endereco, setEndereco] = useState({
    rua: '',
    numero: '',
    complemento: '',
    bairro: '',
    cep: '',
    cidade: '',
    estado: ''
  });

  //valores dos inputs
  const [rua, setRua] = useState('');
  const [numero, setNumero] = useState('');
  const [complemento, setComplemento] = useState('');
  const [bairro, setBairro] = useState('');
  const [cep, setCep] = useState('');
  const [cidade, setCidade] = useState('');
  const [estado, setEstado] = useState('');
  const [sn, setSn] = useState(false);

  const handleClose = () => {
    setOpen(false);
  };

  useEffect(()=>{
    if (props.open){
      setOpen(true);
      buscaEndereco();
    }
  },[props.open]);

  function buscaEndereco (){
    HttpAuth.get('cliente/'+props.id+'/endereco').then(
      (response) => {
        if (response.data !== undefined || response.data === '' || response.data === '[]') {
          setQtdEndereco(response.data.length);

          setEndereco({
            rua: response.data[qtdEndereco-1].rua,
            numero: response.data[qtdEndereco-1].numero,
            complemento: response.data[qtdEndereco-1].complemento,
            bairro: response.data[qtdEndereco-1].bairro,
            cep: response.data[qtdEndereco-1].cep,
            cidade: response.data[qtdEndereco-1].cidade,
            estado: response.data[qtdEndereco-1].estado
          })
        }
      }
    );
  }

  return (<>
    <Modal show={open} onHide={handleClose} size='xl'>
        <Modal.Header closeButton>
          <h2>
            Endereço de entrega
          </h2>
        </Modal.Header>
        <Modal.Body>
          <Row>
            <Form>
              <Form.Group controlId="endereco">
                <Form.Label>Cep</Form.Label>
                <Form.Control 
                  size='sm'
                  type="text"
                  placeholder="12345-123" 
                  value={cep}
                  onChange={text => setCep(text.target.value)}
                />
                <Form.Text className="text-muted">
                  Caso não saiba seu cep: 
                  <a href='https://buscacepinter.correios.com.br/app/endereco/index.php'>
                    Pesquise seu cep
                  </a>
                </Form.Text>
                <Form.Label>Rua</Form.Label>
                <Form.Control
                  size='sm'
                  type="text"
                  placeholder="Rua ciclano da silva"
                  value={rua}
                  onChange={text => setRua(text.target.value)}
                />
                <Form.Label>Número</Form.Label>
                <Form.Control
                  size='sm'
                  type="number"
                  placeholder="10"
                  value={numero}
                  onChange={text => setNumero(text.target.value)}
                />
                <Form.Check 
                  type="switch"
                  id="isSemNumero"
                  label="s/n"
                  value={sn}
                  onChange={text => setSn(text.target.value)}
                />
                <Form.Label>Complemento</Form.Label>
                <Form.Control
                  size='sm'
                  type="text"
                  placeholder="Ap 1001"
                  value={complemento}
                  onChange={text => setComplemento(text.target.value)}
                />
                <Form.Label>Bairro</Form.Label>
                <Form.Control
                  size='sm'
                  type="text"
                  placeholder="Bairro das flores"
                  value={bairro}
                  onChange={text => setBairro(text.target.value)}
                />
                <Form.Label>Cidade</Form.Label>
                <Form.Control
                  size='sm'
                  type="text"
                  placeholder="Cidade dos Anjos"
                  value={cidade}
                  onChange={text => setCidade(text.target.value)}
                />
                <Form.Label>Estado / UF</Form.Label>
                <Form.Control size='sm' type="text" placeholder="BA" value={estado} />
              </Form.Group>
            </Form>
          </Row>
        </Modal.Body>
        <Modal.Footer>
          <Container>
            <Row>
              <Col className='d-flex justify-content-center'>
                <Button variant='success'>
                  Confirmar endereço e iniciar pagamento
                </Button>
              </Col>
            </Row>
          </Container>
        </Modal.Footer>
      </Modal>
  </>);
}
