import { Link } from "react-router-dom";
import style from "./style.css";
import PersonIcon from '@material-ui/icons/Person';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import { index } from '../../store/actions/me.action';
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from 'react';
import { CircularProgress } from '@material-ui/core';
import Cart from '../cart';
import { set_active } from '../../store/actions/cart.action';
import Auth from '../auth';
import { Navbar, Nav } from 'react-bootstrap';

export default function Header() {

  const me = useSelector (state => state.Me);
  const cart = useSelector(state => state.Cart);
  const dispatch = useDispatch();

  function indexCall () {
    dispatch( index() );
  }

  useEffect( () => {
    indexCall();
  }, [me.NAME]);

  //ABERTURA E FECHAMENTO DO LOGIN
  const [opened, setOpened] = useState();

  return (
    <>
      <Cart />
      <Auth opened = {opened} />

      {(window.innerWidth < 577) ?
        <></>
        :
        <Navbar bg="dark" variant="dark" expand="lg">
          <Navbar.Brand className='logo'>
            Nozes Trump
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
                <Nav.Link href='#'>
                  {(me.LOADING) ?
                    <div className="d-flex jultify-content-center">
                      <CircularProgress />
                    </div>
                    :
                    <>{(me.NAME) ?
                        <>Ol√°, {me.NAME}</>                  
                        : 
                        <div className='loginButton' onClick={
                          (e) => { 
                            setOpened(true);
                            setTimeout(()=>{setOpened()}, 3000);
                          }
                        }>
                          <PersonIcon fontSize='default'/>
                          Login
                        </div>
                      }
                    </>
                  }
                </Nav.Link>

                <Nav.Link href='#' onClick={(e)=>{
                  dispatch( set_active(true) );
                }}>
                  <ShoppingCartIcon fontSize='default'/> Carrinho
                </Nav.Link>
              </Nav>
          </Navbar.Collapse>
        </Navbar>  
        }
    </>
  )
}
