export default (state = 'NULL', action) => {
  switch (action.type) {
    
    case 'INDEX_ITEMS':
      return action.payload.map(  (item) =>
        <div className='card' key={item.id}> 
          <div className='card-body'>
            <div className='card-title'>{item.nome}</div>
          </div>
          <div className='card-text'>
            {item.desc}
            {item.preco}
            <button>comprar</button>
          </div>
        </div>
      );

    default:
      return state;
  }
}
