import { Http } from '../../config/Http';
import { changeLoading } from './loading.action';
import { changeNotify } from './notify.action';
import { useDispatch, useSelector } from 'react-redux';

export const actionTypes = {
  CHANGE: 'CHANGE_AUTH',
  SUCCESS: 'SUCCESS_AUTH'
}

export const change = (data) => ({
  type: actionTypes.CHANGE,
  data,
})

export const success = (data) => ({
  type: actionTypes.SUCCESS,
  data
})

export const SetUserToken = token => {
  const dispatch = useDispatch();
  const { success, change } = useSelector(state => state.Auth);

  localStorage.setItem('access_token', token);

  dispatch( change({
    email: '',
    password: ''
  }))

  dispatch(success(true))
}

export const login = credentials => {

  const dispatch = useDispatch();
  const { changeLoading, changeNotify } = useSelector(state => state.Auth);

  dispatch(changeLoading({
    open: true,
    msg: 'Autenticndo usuário...'
  }))

  return Http.post('/login', {
    email: credentials.email,
    password: credentials.password
  }).then( (response)=>{
    if (typeof response !== 'undefined'){
      if(response.data.access_token){
        dispatch( changeLoading({ open: false }) );
        dispatch( SetUserToken(response.data.access_token));
      }
    }
  }).catch(error => {
    if (typeof error.response !== 'undefined'){
      if (error.response.status === 401 || error.response.status === 400){
        dispatch( changeLoading({ open: false }));
        dispatch( changeNotify({
          open: true,
          clss: 'error',
          msg: 'Email e/ou senha incorretos'
        }));
      }
    } else {
      dispatch( changeLoading({ open: false }));
        dispatch( changeNotify({
          open: true,
          clss: 'error',
          msg: 'Erro o se conectar ao servidor'
        }));
    }
  })
}
