import React, { useEffect, useState } from 'react';
import { CircularProgress } from '@material-ui/core';
import Modal from '@material-ui/core/Modal';
import { makeStyles } from '@material-ui/core/styles';
import { useSelector, useDispatch } from 'react-redux';
import { index, photo } from '../../store/actions/items.action';
import { add_items, remove_items, get_cart } from '../../store/actions/cart.action';
import { style } from './style.css';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import { changeNotify } from '../../store/actions/notify.action';
import styled from 'styled-components';

const Categories = styled.div`
  min-height: 6vh;
  background-color: #474a51;
  color: white;
`;

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: '80%',
    height: '60%',
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    top: '20%',
    left: '10%'
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  }
}));

export default function BannerItems(){

  const dispatch = useDispatch();
  const items = useSelector(state => state.Items);

  //Seleçãode itens a ser mostrado
  const [tipoItens, setTipoItens] = useState(0);

  function indexCall() {
    dispatch( index(0) );
  }

  useEffect(()=>{
    dispatch( index(tipoItens) );
  },[tipoItens]);

  //Abertura de itens

  const classes = useStyles();
  // getModalStyle is not a pure function, we roll the style only on the first render
  const [open, setOpen] = useState(false);
  const [item, setItem] = useState({ 
    id: null,
    foto: null, 
    desc: null,
    preco: null
  });
  const [itemQuantity, setItemQuanity] = React.useState(1);

  //input de tamanho e retorno
  const [tamanho, setTamanho] = React.useState(undefined);

  const handleChange = (event) => {
    setTamanho(event.target.value);
  };

  function tamanhoSel(tipo){

    //Tipos de vestimenta:
    //  1 - Camisa Slim (P ao XG)
    //  2 - Camisa Street (M ao XXG)
    //  3 - Chinelo (37 ao 43)
    //  4 - Bermudas e calças (P ao G)
    if (tipo !== 3){
      return (<>
        <FormControl component="fieldset">
          <FormLabel component="legend">TAMANHO</FormLabel>
          <RadioGroup aria-label="tamanho" name="tamanho" value={tamanho} onChange={handleChange}>
            
          { tipo === 1 && <>
              <FormControlLabel value="p" control={<Radio />} label="P" />
            </>}
            <FormControlLabel value="m" control={<Radio />} label="M" />
            <FormControlLabel value="g" control={<Radio />} label="G" />
            { tipo === 1 && <>
              <FormControlLabel value="xg" control={<Radio />} label="XG" />
            </>}
            { tipo === 2 && <>
              <FormControlLabel value="xxg" control={<Radio />} label="XXG" />
            </>}
          </RadioGroup>
        </FormControl>
      </>);
    } else {
      return (<>
        <FormControl className={classes.formControl}>
          <InputLabel id="demo-simple-select-label">TAMANHO</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={tamanho}
            onChange={handleChange}
          >
            <MenuItem value={37}>37</MenuItem>
            <MenuItem value={38}>38</MenuItem>
            <MenuItem value={39}>39</MenuItem>
            <MenuItem value={40}>40</MenuItem>
            <MenuItem value={41}>41</MenuItem>
            <MenuItem value={42}>42</MenuItem>
            <MenuItem value={43}>43</MenuItem>
          </Select>
        </FormControl>
      </>);
    }
  }
  //!imput de tamanho e retorno

  const handleOpen = (id, foto, desc, preco, tipo) => {
    setOpen(true);
    setItem({ id: id,
              foto: foto, 
              desc: desc,
              preco: preco,
              tipo: tipo
            });
  };

  const handleClose = () => {
    setOpen(false);
  };

  //!Abertura de itens

  useEffect (() => {
    if (!items.LOADING){
      for(let i = 0; i < items.ITEMS.length; i = i + 1 ) {

        let items_id = [];
        if (items.ITEMS[i].id !== undefined){
          //dispatch( photo( items.ITEMS[i].id ) );
          items_id.push(items.ITEMS[i].id);
        }
        console.log(items_id);
      }
    }
  }, [items.LOADING]);

  useEffect( () => {
    indexCall();
  }, []);

  return (
    <div className="container pt-5">
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        <div className={'row d-flex align-items-center '.concat(classes.paper)}>
          <div className='col-md-6 d-flex align-items-center' height='100%'>
            <img src={item.foto} height='100%'></img>
          </div>
          <div className='col-md-6 d-flex flex-column align-items-center justify-content-around item-display'>
            <h2>
              {item.desc}
            </h2>
            <div>
              {tamanhoSel(item.tipo)}
            </div>
            <div>
              <h3>
                R$ {item.preco}
              </h3>
            </div>
            <button
            onClick={()=>{
              if (tamanho === undefined){
                dispatch( changeNotify({
                  open: true,
                  class: 'error',
                  msg: 'Selecione um tamanho.'
                }));
              } else {
                dispatch( add_items(item.id, 1, item.foto, item.desc, item.preco, tamanho));
                setTamanho(undefined);
                handleClose();
              }
            }}
            >
              Adicionar ao carrinho
            </button>
          </div>
        </div>
      </Modal>

      <Categories className='container-fluid'>
        <div className='row'>
          <button className='col-3 d-flex justify-content-center pt-2 pb-2'
            onClick={(e)=>{setTipoItens(1)}}
          >
            Camisas Slim
          </button>
          <button className='col-3 d-flex justify-content-center pt-2 pb-2'
            onClick={(e)=>{setTipoItens(2)}}
          >
            Camisas Street
          </button>
          <button className='col-3 d-flex justify-content-center pt-2 pb-2'
            onClick={(e)=>{setTipoItens(3)}}
          >
            Calçados
          </button>
          <button className='col-3 d-flex justify-content-center pt-2 pb-2'
            onClick={(e)=>{setTipoItens(4)}}
          >
            Bermudas e calças
          </button>
        </div>
      </Categories>

      <div className="row justify-content-center">
        <div className="col-auto" onClick={(e)=>{setTipoItens(0)}}>
          <h1>
            Últimas novidades
          </h1>
        </div>
      </div>
      <div className="row justify-content-center">
        {items.LOADING ? <CircularProgress />
        :
        <>
          {typeof(items.ITEMS) === "object" ?
            items.ITEMS.map( (item) =>
              <div
                key={item.id}
                className='col-md-4 justify-content-center d-flex card'
              >
                <div className='row d-flex flex-row justify-content-center'>
                  {items.LOADING_PHOTO ? <CircularProgress />
                  :
                    <>{items.PHOTO.map( (photo) => <>{photo.id !== item.id ? ''
                      :
                      <img
                      key={photo.id}
                      className='p-2 foto'
                      src={'http://api-lojabim.test/'.concat([photo.img])}
                      onClick={(e) => handleOpen(item.id, 'http://api-lojabim.test/'.concat([photo.img]), item.desc, item.preco, item.tipo)}
                      ></img>
                    }</>)}</>
                  }
                </div>
                <div className='row d-flex flex-row desc'>
                  <div className="pl-5">
                    {item.desc}
                  </div>
                </div>
                <div className='row d-flex flex-row preco'>
                  <div className="pl-5">
                    R${item.preco}
                  </div>
                </div>
              </div>
            )
            :
            <>
              Não há itens nessa categoria.
            </>
          }
        </>
        }
      </div>
    </div>
  )
}
