import React, { useEffect, useState } from 'react';
import { Carousel } from 'react-bootstrap';
import { useSelector } from 'react-redux';
import { photo } from '../../store/actions/items.action';
import { CircularProgress } from '@material-ui/core';

export default function ShowPhotos(props){

  const items = useSelector(state => state.Items);

  const slides = items.PHOTO[0].filter((item => item.id === props.id).map(filteredItems => (
    <Carousel.item>
        <img
          key={photo.id}
          className='p-2 foto d-block w-100'
          src={'http://api-lojabim.test/'.concat([photo.img])}
        ></img>
    </Carousel.item>
  )));

  return (<>
    <Carousel>{items.PHOTO[0].map( (photo) => <>{photo.id !== props.id ?
        ''
      :
        {slides}
      }</>)}</Carousel>
  </>)
}
