import { actionTypes } from '../actions/cart.action';

const initialState = {
  ITEMS: [],
  ACTIVE: false
}

export default (state = initialState, {type, payload}) => {
  switch (type){

    case actionTypes.ADD_ITEM:
      let arr_items = state.ITEMS;
      let items = state.ITEMS.findIndex( item => item.id == payload.id );

      if(items < 0) {
        arr_items.push({
          id: payload.id,
          qtd: payload.qtd,
          img: payload.img,
          nome: payload.desc,
          preco: payload.preco
        });
      } else {
        arr_items.push({
          id: payload.id,
          qtd: state.ITEMS[items].qtd + 1,
          img: payload.img,
          nome: payload.desc,
          preco: payload.preco
        });
        arr_items.splice(items, 1);
      }

      return {...state, ITEMS:  arr_items}
    

    case actionTypes.REMOVE_ITEM:
      let remove_arr_items = state.ITEMS;
      let remove_items = state.ITEMS.findIndex( item => item.id == payload.id );

      if(state.ITEMS[remove_items].qtd < 2) {
        remove_arr_items.splice(remove_items, 1);
      } else {
        remove_arr_items.push({
          id: state.ITEMS[remove_items].id,
          qtd: state.ITEMS[remove_items].qtd - 1,
          img: state.ITEMS[remove_items].img,
          nome: state.ITEMS[remove_items].desc,
          preco: state.ITEMS[remove_items].preco
        });
        remove_arr_items.splice(remove_items, 1);
      }

      return {...state, ITEMS:  remove_arr_items}

    case actionTypes.SET_CART:
      localStorage.removeItem('storeCart');
      localStorage.setItem('storeCart', JSON.stringify(state.ITEMS));

    case actionTypes.GET_CART:
      let cart = localStorage.getItem('storeCart');
      if (cart !== null) {
        return {...state, ITEMS: JSON.parse(cart)}
      }

    case actionTypes.SET_ACTIVE:
      if (state.ACTIVE === false) {
        return {...state, ACTIVE: true}
      } else if (state.ACTIVE === true) {
        return {...state, ACTIVE: false}
      }
      

    default:
      return state
  }
}
