import { actionTypes } from '../actions/items.action';

const initialState = {
  items: {
    data:[]
  },
  item: {

  },
  success: false,
  error: {}
}

export default (state = initialState, { type, payload, isLoadMore }) => {
  switch (type) {

    case actionTypes.CHANGE:
      return { 
        ...state,
        item: {
          ...state.item,
          payload
        }
      }
    
    case actionTypes.INDEX:
      
      return { ...state, success: payload }

    case actionTypes.DESTROY:
      return {
        ...state,
        items:{
          ...state.items,
          payload: state.items.data.filter(item => item.id !== payload)
        }
      }

    case actionTypes.SUCCESS:
      return { ...state, success: payload }

    case actionTypes.ERROR:
      return { ...state, error: payload }

    default:
      return state
  }
}
