import { actionTypes } from '../actions/items.action';

const initialState = {
  items: {
    data:[]
  },
  item: {

  },
  success: false,
  error: {}
}

export default (state = initialState, { type, data, isLoadMore }) => {
  switch (type) {

    case actionTypes.CHANGE:
      return { 
        ...state,
        item: {
          ...state.item,
          data
        }
      }
    
    case actionTypes.INDEX:
      console.log(data.map((item)=>item.id))
      return { ...state, items: { data: [data.map((item)=>item.id)] } ,...data }

    case actionTypes.DESTROY:
      return {
        ...state,
        items:{
          ...state.items,
          data: state.items.data.filter(item => item.id !== data)
        }
      }

    case actionTypes.SUCCESS:
      return { ...state, success: data }

    case actionTypes.ERROR:
      return { ...state, error: data }

    default:
      return state
  }
}
